import React, { useEffect, useState } from "react";
import Display from "./Display";
import axios from "axios";

const GetData = ({ id }) => {
  // await axios.get(url, { params: { answer: 42 } });
  // url?params=answer, example: ...search?query=story
  const [item, setItem] = useState();
  const [query, setQuery] = useState(id);

  const fetchdata = () => {
    if (!query) {
      setQuery("story");
    }
    try {
      const res = axios({
        url: "http://hn.algolia.com/api/v1/search",
        method: "get",
        timeout: 8000,
        params: { query: { query } },
        headers: {
          "Content-Type": "application/json",
        },
      });

      res
        .then((response) => response.data)
        .then((data) => {
          setItem(data.hits);
        });
      console.log("try");
    } catch (err) {
      if (err.response) {
        // The client was given an error response (5xx, 4xx)
        // console.log(err.response.data);
        // console.log(err.response.status);
        // console.log(err.response.headers);
        console.log("Error: " + err.message);
      }
    }
  };

  useEffect(() => {
    fetchdata();
  }, [id]);

  //item[i].author,item[i].title,item[i].num_comments,item[i].points
  idnr = 0;
  return (
    <div id="ul">
      <Display
        idnr={idnr}
        username={item[idnr].author}
        title={item[idnr].title}
        comments={item[idnr].num_comments}
        points={item[idnr].points}
      />
      <Display
        username={item[idnr].author}
        title={item[idnr].title}
        comments={item[idnr].num_comments}
        points={item[idnr].points}
      />
    </div>
  );
};

export default GetData;
