import React, { useEffect, useState, useRef } from "react";
import Display from "./Display";
import axios from "axios";

const GetData = ({ id }) => {
  // await axios.get(url, { params: { answer: 42 } });
  // url?params=answer, example: ...search?query=story
  const [item, setItem] = useState();
  const [query, setQuery] = useState(id);

  useEffect(() => {
    if (!query) {
      setQuery("story");
    }
    try {
      const res = axios({
        url: "http://hn.algolia.com/api/v1/search",
        method: "get",
        timeout: 8000,
        params: { query: { query } },
        headers: {
          "Content-Type": "application/json",
        },
      });
      const obj = res
        .then((response) => response.data)
        .then((data) => {
          setItem(data.hits);
          //console.log(item);
        });
      return obj.hits, setItem;
    } catch (err) {
      if (err.response) {
        console.log("Error: " + err.message);
      }
    }
  }, [query]);

  //item[i].author,item[i].title,item[i].num_comments,item[i].points
  console.log(item);

  return (
    <div id="ul">
      <>
        <h4>{item[0].titel}</h4>
        <ul>
          <li>{item[0].points} points</li>
          <li>by {item[0].author}</li>
          <li>{item[0].num_comments} comments</li>
        </ul>
      </>
      {/* <Display
        username={item[idnr].author}
        title={item[idnr].title}
        comments={item[idnr].num_comments}
        points={item[idnr].points}
      /> */}
    </div>
  );
};

export default GetData;
