import React, { useEffect, useState } from "react";
import Display from "./Display";
import axios from "axios";

const GetData = ({ id }) => {
  // await axios.get(url, { params: { answer: 42 } });
  // url?params=answer, example: ...search?query=story
  const [item, setItem] = useState();
  const [query, setQuery] = useState(id);
  const [title, setTitles] = useState("");

  const fetchdata = () => {
    if (!query) {
      setQuery("story");
    }
    try {
      axios({
        url: "http://hn.algolia.com/api/v1/search",
        method: "get",
        timeout: 8000,
        params: { query: { query } },
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((response) => response.data)
        .then((data) => {
          setItem(data.hits);
        });
      console.log("try");
    } catch (err) {
      if (err.response) {
        // The client was given an error response (5xx, 4xx)
        // console.log(err.response.data);
        // console.log(err.response.status);
        // console.log(err.response.headers);
        console.log("Error: " + err.message);
      }
    }
  };

  useEffect(() => {
    fetchdata();
  }, []);
  console.log(item);
  //93 points by fortran77 1 hour ago | hide | 39 comments

  return (
    <div id="ul">
      <Display
        username={"author"}
        title={item[0].title}
        comments={item[0].num_comments}
        hours={item[0].created_at}
        points={item[0].points}
      />
    </div>
  );
};

export default GetData;
