import React, { useEffect, useState } from "react";
import axios from "axios";

const GetData = ({ id }) => {
  // await axios.get(url, { params: { answer: 42 } });
  // url?params=answer, example: ...search?query=story
  const [item, setItem] = useState([]);
  const [query, setQuery] = useState(id);
  let date = new Date();
  // const format = require("dateformat");

  useEffect(() => {
    console.log(query);
    const fetchdata = () => {
      // if (!query) {
      //   setQuery("story");
      // }
      try {
        console.log();
        const res = axios({
          url: "http://hn.algolia.com/api/v1/search?" + { id },
          method: "get",
          // timeout: 1000,
          // params: { query: { id } },
          headers: {
            "Content-Type": "application/json",
          },
        });
        res
          .then((response) => response.data)
          .then((data) => {
            setItem(data.hits);
            // setMaintitle(data.hits[0]);
          });
      } catch (err) {
        if (err.response) {
          console.log("Error: " + err.message);
        }
      }
    };
    fetchdata();
    // setTimeout(() => {
    //   console.log("This will run after 1 second!");
    // }, 1000);
  }, []);
  // console.log(item);
  //item[i].author,item[i].title,item[i].num_comments,item[i].point
  //date: format(new Date(item[i].created_at),'dd MMMM yyyy');
  return (
    <div>
      {item.map(({ author, created_at, title, objectId, url }) => (
        <div key={objectId}>
          <div id="ul">
            <h4>{title}</h4>
            <ul>
              <li>posted by {author}</li>
              <li>on </li>
              <li>
                <a href={url} target="_blank">
                  go to Article
                </a>
              </li>
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
};

export default GetData;
